var parallel = require('../../lib/parallel');

parallel("Test Suit Collection", function() {
    var tmp = 0;
    afterEach("cleanUp", function() {
        console.log(tmp);
    });

    it("test 1", async function() {
        await sleep(1000);
        expect(true).to.be.false;
        tmp = 1;
    });

    it("test 2", async function() {
        await sleep(1000);
        expect(true).to.be.true;
        tmp = 2;
    });
});
